    }    function doPrint(){        var printHead = "<html><head><meta http-equiv='Content-Type' content='text/html; charset=utf-8'>" +"<link rel='stylesheet'  type='text/css' href='css/printcss.css'></head><body>";        var printwindow = window.open("", "newwin", "height=" + window.screen.height + ",width=" + window.screen.width + ",toolbar=no,scrollbars=auto,menubar=no");        printwindow.document.write(printHead + document.getElementById('printArea').innerHTML);        printwindow.document.close();//这个是必须的        printwindow.window.print();//        printwindow.document.body.innerHTML = document.getElementById('printArea').innerHTML;//        printwindow.window.print();    //Firefox    }</script><?php (!empty($this) && $this instanceof WeModuleSite || 1) ? (include $this->template('_footer', TEMPLATE_INCLUDEPATH)) : (include template('_footer', TEMPLATE_INCLUDEPATH));?>